DEFAULT SALARY := 120000;
DEFAULT STATE := 'GA';
DEFAULT GRADE := 'MGR';

-- Calculate high-income tax
HIGH_INCOME_TAX := IF SALARY > 120000 THEN SALARY * 0.03 ELSE 0;

-- Calculate mid-income tax
MID_INCOME_TAX := IF SALARY > 80000 AND SALARY <= 120000 THEN SALARY * 0.02 ELSE 0;

-- Calculate low-income tax
LOW_INCOME_TAX := IF SALARY <= 80000 THEN SALARY * 0.01 ELSE 0;

-- Calculate state fee for California
STATE_FEE := IF STATE = 'CA' THEN 50 ELSE 0;

-- Calculate executive bonus based on job grade
EXECUTIVE_BONUS := IF GRADE IN ('MGR', 'DIR', 'VP', 'CEO') THEN 500 ELSE 0;

-- Calculate net pay
NET_PAY := SALARY - HIGH_INCOME_TAX - MID_INCOME_TAX - LOW_INCOME_TAX + STATE_FEE + EXECUTIVE_BONUS;

-- Return the final net pay
NET_PAY
